export function InteractiveMap() {
  const [activeVillage, setActiveVillage] = useState<Village | null>(null);
  const [hoveredVillage, setHoveredVillage] = useState<string | null>(null);

  return (
    <div className="relative w-full h-[65vh] bg-slate-50/30 rounded-2xl overflow-hidden border">
      
      {/* Helper Text (Bottom Left) */}
      <div className="absolute bottom-6 left-6 z-10 bg-white/80 backdrop-blur px-4 py-2 rounded-full text-sm text-muted-foreground shadow-sm pointer-events-none border">
        Klik wilayah untuk detail
      </div>

      {/* Map Area */}
      <div className="w-full h-full flex items-center justify-center p-0 md:pr-[450px] pb-12 md:pb-0">
        <svg
          viewBox="0 0 3507 2480"
          className="w-full h-full drop-shadow-xl"
          style={{ filter: "drop-shadow(0px 10px 15px rgba(0,0,0,0.1))" }}
          preserveAspectRatio="xMidYMid meet"
        >
          <g fill="none" fillRule="evenodd" stroke="none" strokeWidth="1" strokeLinecap="square" strokeLinejoin="bevel">
            {villages.map((village) => (
              <path
                key={village.id}
                d={village.path}
                fill={village.color}
                stroke="#ffffff"
                strokeWidth={activeVillage?.id === village.id ? "10" : "2"}
                className={cn(
                  "transition-all duration-300 cursor-pointer hover:opacity-90 outline-none",
                  activeVillage?.id === village.id ? "opacity-100 brightness-110 drop-shadow-2xl scale-[1.01] z-10" : (activeVillage ? "opacity-30" : "opacity-100")
                )}
                onClick={() => setActiveVillage(village)}
                onMouseEnter={() => setHoveredVillage(village.name)}
                onMouseLeave={() => setHoveredVillage(null)}
                style={{
                  transformBox: "fill-box",
                  transformOrigin: "center",
                }}
              />
            ))}
          </g>
        </svg>
      </div>

      {/* Hover Tooltip (Mouse Follower style or fixed bottom right) */}
      {hoveredVillage && !activeVillage && (
        <div className="absolute bottom-6 right-6 bg-black/80 backdrop-blur text-white px-4 py-2 rounded-full text-sm font-medium pointer-events-none z-30 transition-opacity">
          {hoveredVillage}
        </div>
      )}

      {/* Floating Info Panel (Top Right) */}
      <div className="absolute top-4 right-4 w-80 md:w-[420px] flex flex-col gap-3 z-20 max-h-[calc(100%-2rem)] overflow-y-auto pr-1 scrollbar-hide">
        
        {/* Detail Box */}
        <motion.div 
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
            className="bg-white/90 backdrop-blur-md rounded-xl border shadow-lg p-5"
        >
            <h3 className="text-xl font-bold text-secondary mb-1">
                {activeVillage ? activeVillage.name : "Info Wilayah"}
            </h3>
            <div className="h-1 w-12 bg-primary mb-3 rounded-full" />
            
            {activeVillage ? (
                <div className="animate-fade-in">
                    <p className="text-base text-muted-foreground leading-relaxed">
                        {activeVillage.description}
                    </p>
                </div>
            ) : (
                <p className="text-sm text-muted-foreground italic">
                    Pilih wilayah di peta untuk melihat detail.
                </p>
            )}
        </motion.div>

        {/* Legend Box (Compact) */}
        <div className="bg-white/90 backdrop-blur-md rounded-xl p-5 border shadow-lg">
            <h4 className="font-semibold text-xs uppercase tracking-wider text-muted-foreground mb-3">Legenda</h4>
            <div className="grid grid-cols-2 gap-x-4 gap-y-2">
                {villages.map(v => (
                    <div 
                        key={v.id} 
                        className={cn(
                            "flex items-center gap-2 text-xs transition-colors p-1 rounded cursor-pointer",
                            activeVillage?.id === v.id ? "bg-muted font-medium" : "hover:bg-muted/50 text-muted-foreground"
                        )}
                        onClick={() => setActiveVillage(v)}
                    >
                        <span className="w-2.5 h-2.5 rounded-full shadow-sm flex-shrink-0" style={{ backgroundColor: v.color }} />
                        <span className="truncate">{v.name}</span>
                    </div>
                ))}
            </div>
        </div>
      </div>
    </div>
  );
}
